import { CloudUpload } from "@mui/icons-material";
import React, { useState } from "react";

export const UploadPage = () => {
  const [file, setFile] = useState(null);
  const onFileChange = (e) => {
    setFile(e.target.files[0]);
  };
  const onFileUpload = (e) => {
    const formData = new FormData();
    formData.append("new-file", file, file.name);
    console.log(file);
  };
  return (
    <div className="mt-20 px-10 sm:px-20 py-10">
      <div className="flex gap-10">
        <div class="shadow-lg w-full md:w-1/2 bg-white rounded-2xl mt-10 p-6 pt-10 flex flex-col justify-center items-center gap-10">
          <h1 className="text-3xl font-extrabold font-titleFont text-sky-950">
            Upload Files
          </h1>
          <label
            htmlFor="dropzone-file"
            class="flex flex-col items-center justify-center w-full h-64 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 dark:hover:bg-bray-800 dark:bg-gray-700 hover:bg-gray-100 dark:border-gray-600 dark:hover:border-gray-500 dark:hover:bg-gray-600"
          >
            <div class="flex flex-col items-center justify-center pt-5 pb-6">
              <span className=" text-sky-600">
                <CloudUpload sx={{ fontSize: 72 }} />
              </span>
              <p class="mb-2 text-base text-center text-gray-500 dark:text-gray-400">
                <span class="font-semibold">Click to upload</span> or drag and
                drop
              </p>
            </div>
            <input id="dropzone-file" type="file" class="hidden" />
          </label>
        </div>
        <div class="shadow-lg w-full md:w-1/2 bg-white rounded-2xl mt-10 p-6 pt-10 flex flex-col  gap-10">
          <h1 className="text-3xl font-extrabold font-titleFont text-sky-950">
