import { Cloud, CloudUpload } from "@mui/icons-material";
import React from "react";

export const UploadPage = () => {
  return (
    <div className="mt-20 px-20 py-10">
      <div className="w-1/2 bg-white rounded-2xl p-6 flex flex-col justify-center items-center gap-10">
        <h1 className="text-3xl font-extrabold font-titleFont text-sky-950">
          Upload Files
        </h1>
        <div className="bg-sky-50 rounded-2xl w-full py-36 text-sky-950 flex flex-col gap-8 items-center justify-center">
          <CloudUpload sx={{ fontSize: 96 }} />
          <div className="flex flex-col gap-2 justify-center items-center">
            <span className="font-bodyFont text-2xl font-semibold text-sky-900">
              Drag & drop your files here
            </span>
            <span className="text-sm">or</span>
            <button className="border-sky-900 border rounded-md p-2">
              Choose files from your computer
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
