import React from "react";
import { userImg } from "../assets";
import { FaUserLock, FaUsers, FaCloudUploadAlt } from "react-icons/fa";
import { MdFavorite } from "react-icons/md";

export const Sidebar = () => {
  return (
    <div className="h-screen fixed top-0 left-0 w-48 bg-cyan-900 flex flex-col justify-between py-10 text-white font-titleFont tracking-wide">
      <div className="flex flex-col gap-8 items-center">
        <div className=" flex justify-center items-center w-full">
          <img src={userImg} className="w-20 rounded-full" alt="" />
        </div>
        <div className="flex flex-col text-base ">
          <span className="flex gap-4 py-3 px-4 items-center duration-200 cursor-pointer hover:bg-cyan-950">
            <FaUserLock />
            My Vault
          </span>
          <span className="flex gap-4 py-3 px-4 items-center duration-200 cursor-pointer hover:bg-cyan-950">
            <FaUsers />
            Shared files
          </span>
          <span className="flex gap-4 py-3 px-4 items-center duration-200 cursor-pointer hover:bg-cyan-950">
            <MdFavorite />
            Favorites
          </span>
          <span className="flex gap-4 py-3 px-4 items-center duration-200 cursor-pointer hover:bg-cyan-950">
            <FaCloudUploadAlt />
            Upload files
          </span>
        </div>
      </div>
    </div>
  );
};
