import React, { useEffect, useState } from "react";

export const Menubar = () => {
  const [file, setFile] = useState([]);
  const [fileURL, setFileURL] = useState([]);
  // let file = [];
  // let fileURL = [];
  let imgSection;

  const upload = (e) => {
    for (let i = 0; i < e.target.files.length; i++) {
      file.push(e.target.files[i]);
      fileURL.push(URL.createObjectURL(e.target.files[i]));
      // imgSection += (
      //   <img
      //     src={URL.createObjectURL(e.target.files[i])}
      //     alt=""
      //     className="aspect-video w-48 object-cover"
      //   />
      // );
      // console.log(fileURL);
    }
    setFile(file);
    setFileURL(fileURL);
  };

  return (
    <div className="mt-10 ml-10">
      <input type="file" name="file" id="file" onChange={upload} multiple />
      {fileURL.length ? (
        fileURL.map((item) => {
          return (
            <img src={item} alt="" className="aspect-video w-48 object-cover" />
          );
        })
      ) : (
        <div>No files</div>
      )}
    </div>
  );
};
