import React, { useEffect, useState } from "react";
import { FcGoogle } from "react-icons/fc";
import { GrGithub } from "react-icons/gr";
import { AiOutlineUserAdd } from "react-icons/ai";
import { FaUserShield } from "react-icons/fa";
import { logo } from "../assets";
import { useAuthState } from "react-firebase-hooks/auth";
import { Link, useNavigate } from "react-router-dom";
import {
  auth,
  googleProvider,
  signInWithEmail,
  signInWithGithub,
  signInWithGoogle,
} from "../firebase";
import { Backdrop, CircularProgress } from "@mui/material";
import { signInWithPopup } from "firebase/auth";
export const Login = () => {
  const [registered, setRegistered] = useState(false);
  const signin = () => {
    setRegistered(true);
  };
  const signup = () => {
    setRegistered(false);
  };
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loader, setLoader] = useState(false);

  const [user, loading] = useAuthState(auth);
  const navigate = useNavigate();
  // useEffect(() => {
  //   if (loading) {
  //     setLoader(true);
  //     return;
  //   }
  //   if (user) {
  //     setLoader(false);
  //     navigate("/cloud");
  //   }

  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  // }, [user, loading]);
  const loginUser = () => {
    setLoader(true);
    signInWithEmail(email, password).then(setLoader(false));
  };

  const handleSigninWithGoogle = (e) => {
    setLoader(true);
    e.preventDefault();
    signInWithPopup(auth, googleProvider)
      .then((result) => {
        // The signed-in user info.
        const user = result.user;
        console.log(user);
        // IdP data available using getAdditionalUserInfo(result)
        // ...
      })
      .then(() => {
        navigate("/cloud");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      })
      .catch((error) => {
        // Handle Errors here.
        const errorCode = error.code;
        const errorMessage = error.message;
        // The email of the user's account used.
        const email = error.customData.email;
        // The AuthCredential type that was used.
        console.log(errorCode, errorMessage, email);
        // ...
      });
  };
  const handleSigninWithGithub = (e) => {
    setLoader(true);
    e.preventDefault();
    signInWithGithub()
      .then(() => {
        navigate("/cloud");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });
  };

  return (
    <>
      <div className="flex flex-col gap-10">
        <div className="flex flex-col gap-2">
          <img src={logo} className="rounded-full w-16 mx-auto mb-4" alt="" />
          {!registered ? (
            <>
              <h1 className="text-3xl font-titleFont font-extrabold text-sky-950 text-center">
                Create Account !
              </h1>
              <h1 className="text-2xl font-titleFont font-extrabold text-sky-900 text-center">
                Sign up for DataVault
              </h1>
            </>
          ) : (
            <>
              <h1 className="text-3xl font-titleFont font-extrabold text-sky-950 text-center">
                Welcome Back !
              </h1>
              <h1 className="text-2xl font-titleFont font-extrabold text-sky-900 text-center">
                Sign in to your vault
              </h1>
            </>
          )}
        </div>
        <div className="flex flex-col justify-center items-center gap-4">
          <button
            onClick={handleSigninWithGoogle}
            className="font-bodyFont text-base font-bold text-sky-950 hover:bg-sky-700 duration-200 hover:text-sky-50 rounded-xl px-4 py-2 border border-sky-700 flex items-center justify-center gap-4 w-4/5 "
          >
            <span className="rounded-full p-[2px] bg-white">
              <FcGoogle className="text-2xl" />
            </span>
            Sign In with Google
          </button>
          <button
            onClick={handleSigninWithGithub}
            className="font-bodyFont text-base font-bold text-sky-950 hover:bg-sky-700 duration-200 hover:text-sky-50 rounded-xl px-4 py-2 border border-sky-700 flex items-center justify-center gap-4 w-4/5 "
          >
            <span className="rounded-full p-[2px] bg-white">
              <GrGithub className="text-2xl text-black" />
            </span>
            Sign In with Github
          </button>
        </div>
        <div className="flex justify-between items-center gap-2">
          <div className="border-[0.5px] h-0 w-full"></div>
          <div className="w-full whitespace-nowrap text-xs text-gray-400">
            Or {!registered ? "signup" : "log in"} with email
          </div>
          <div className="border-[0.5px] h-0 w-full"></div>
        </div>
        <div className="flex flex-col gap-4 justify-center items-center">
          <input
            type="text"
            className="bg-gray-100 border-none px-6 outline-none focus-visible:shadow placeholder:text-gray-400 py-3 w-4/5 rounded-lg text-sm"
            placeholder="Email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            className="bg-gray-100 border-none px-6 outline-none focus-visible:shadow placeholder:text-gray-400 py-3 w-4/5 rounded-lg text-sm"
            placeholder="Password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {!registered ? (
            <button className="font-titleFont text-sm text-white hover:bg-sky-600 rounded-lg px-6 py-3 bg-sky-500 flex items-center justify-center gap-4 w-4/5 ">
              <AiOutlineUserAdd className="text-xl" />
              Sign Up
            </button>
          ) : (
            <button
              onClick={loginUser}
              className="font-titleFont text-sm text-white hover:bg-sky-600 rounded-lg px-6 py-3 bg-sky-500 flex items-center justify-center gap-4 w-4/5 "
            >
              <FaUserShield className="text-xl" />
              Login
            </button>
          )}
        </div>
        {!registered ? (
          <div
            onClick={signin}
            className="text-center font-medium w-fit justify-center items-center mx-auto flex flex-col gap-1 text-sm text-sky-700 font-titleFont cursor-pointer after:w-full after:h-[1px] after:bg-sky-950 after:scale-x-0 duration-200 after:duration-200 hover:after:scale-x-100 hover:text-sky-950"
          >
            Already a user? Login
          </div>
        ) : (
          <div
            onClick={signup}
            className="text-center font-medium w-fit justify-center items-center mx-auto flex flex-col gap-1 text-sm text-sky-700 font-titleFont cursor-pointer after:w-full after:h-[1px] after:bg-sky-950 after:scale-x-0 duration-200 after:duration-200 hover:after:scale-x-100 hover:text-sky-950"
          >
            Don't have an account? Sign Up
          </div>
        )}
      </div>
      {loader && (
        <Backdrop
          sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
          open={loader}
        >
          <CircularProgress color="inherit" />
        </Backdrop>
      )}
    </>
  );
};
