import { CloudUpload } from "@mui/icons-material";
import React, { useState } from "react";
// import {
//   getStorage,
//   ref,
//   uploadBytesResumable,
//   getDownloadURL,
// } from "firebase/storage";
import { app } from "../../firebase";
import { BiExpand } from "react-icons/bi";
import { MdOutlineDelete } from "react-icons/md";
import { Tooltip } from "@mui/material";
import { ImagePreview } from "../../components/ImagePreview";

export const UploadPage = () => {
  const [preview, setPreview] = useState(false);
  const [expand, setExpand] = useState();

  const [file, setFile] = useState([]);

  let output = { files: [], filesURL: [] };
  const onFileChange = (e) => {
    for (let i = 0; i < e.target.files.length; i++) {
      e.target.files[i]["imgURL"] = URL.createObjectURL(e.target.files[i]);
      output["files"].push(e.target.files[i]);
      setFile(output["files"]);
    }
  };
  const show = () => {
    console.log(file);
  };
  // const upload = () => {
  //   const storage = getStorage(app);
  //   const storageRef = ref(storage, `public/${file.name}`);
  //   uploadBytesResumable(storageRef, file).then((snapshot) => {
  //     console.log("Uploaded a blob or file!" + snapshot);
  //   });
  //   uploadBytesResumable(storageRef, file).on(
  //     "state_changed",
  //     (snapshot) => {
  //       const progress =
  //         (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
  //       console.log(`${progress} % done.`);
  //       switch (snapshot.state) {
  //         case "paused":
  //           console.log("paused");
  //           break;
  //         case "running":
  //           console.log("running");
  //           break;
  //         default:
  //           console.log("default");
  //       }
  //     },
  //     (err) => {
  //       switch (err.code) {
  //         case "storage/unauthorized":
  //           console.log("error - unauthorized");

  //           break;
  //         case "storage/canceled":
  //           console.log("error - cancelled");

  //           break;

  //         // ...

  //         case "storage/unknown":
  //           console.log("unknown error");
  //           // Unknown error occurred, inspect error.serverResponse
  //           break;
  //         default:
  //           console.log("error");
  //       }
  //     },
  //     () => {
  //       // Upload completed successfully, now we can get the download URL
  //       getDownloadURL(
  //         uploadBytesResumable(storageRef, file).snapshot.ref
  //       ).then((downloadURL) => {
  //         console.log("File available at", downloadURL);
  //       });
  //     }
  //   );
  // };
  return (
    <div className="mt-14 px-10 sm:px-20 py-10">
      <div className="flex gap-10">
        <div class="shadow-lg w-full md:w-1/2 bg-white dark:bg-gray-700 rounded-2xl mt-10 px-6 py-10 flex flex-col justify-center items-center gap-10">
          <h1 className="text-3xl font-extrabold font-titleFont text-sky-950 dark:text-sky-50">
            Upload Files
          </h1>
          <label
            htmlFor="dropzone-file"
            class="flex flex-col items-center justify-center w-full h-96 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 dark:hover:bg-bray-800 dark:bg-gray-800 hover:bg-gray-100 dark:border-gray-600  dark:hover:bg-gray-900 duration-200"
          >
            <div class="flex flex-col items-center justify-center pt-5 pb-6">
              <span className=" text-sky-600 dark:text-sky-800">
                <CloudUpload sx={{ fontSize: 72 }} />
              </span>
              <p class="mb-2 text-base text-center text-gray-500 dark:text-gray-400">
                <span class="font-semibold">Click to upload</span> or drag and
                drop
              </p>
            </div>
            <input
              id="dropzone-file"
              type="file"
              class="hidden"
              onChange={onFileChange}
              multiple
            />
          </label>
        </div>
        <div class="shadow-lg w-full md:w-1/2 bg-white  dark:bg-gray-700 rounded-2xl mt-10 px-6 py-10 flex flex-col  gap-6">
          <h1 className="text-3xl font-extrabold font-titleFont text-sky-950 dark:text-sky-50 flex items-center gap-2">
            Selected Files{" "}
            {file.length ? (
              <span className="font-bodyFont text-2xl font-medium">{`(${file.length})`}</span>
            ) : (
              <></>
            )}
          </h1>
          <div className="border dark:border-gray-500 h-96 rounded-2xl overflow-auto">
            {file.length ? (
              <div className="grid grid-cols-1 p-3 gap-x-2 gap-y-4">
                {file.map((item) => {
                  return (
                    <div className="shadow-sm border flex items-center  rounded-lg overflow-hidden  dark:border-gray-500 dark:text-sky-50">
                      <span className="w-20 border-r dark:border-gray-500">
                        <img
                          src={item.imgURL}
                          alt=""
                          className="w-full object-contain aspect-square"
                        />
                      </span>
                      <div className="flex justify-between items-center w-full pr-4">
                        <div className="text-xs py-1  overflow-hidden text-ellipsis whitespace-nowrap w-4/5 px-4">
                          <span className="text-center overflow-hidden text-ellipsis whitespace-nowrap">
                            {item.name}
                          </span>
                        </div>
                        <div className="flex gap-8 items-center">
                          <Tooltip title="Expand">
                            <span
                              onClick={() => {
                                setExpand(item);
                                setPreview(true);
                              }}
                              className=" text-lg duration-200 hover:bg-gray-200 dark:hover:bg-gray-500 cursor-pointer p-2 rounded-full"
                            >
                              <BiExpand />
                            </span>
                          </Tooltip>
                          <Tooltip title="Delete">
                            <span className="text-xl duration-200 hover:bg-red-200 dark:hover:bg-red-500/50 cursor-pointer p-2 rounded-full">
                              <MdOutlineDelete />
                            </span>
                          </Tooltip>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="h-full flex justify-center items-center text-2xl font-medium text-gray-400">
                No files chosen
              </div>
            )}
          </div>
          <button
            className="border rounded-xl font-medium uppercase font-titleFont tracking-wider border-sky-800 dark:border-sky-400 text-sky-800 dark:text-sky-400 hover:bg-sky-800 dark:hover:bg-sky-400 hover:text-sky-50 dark:hover:text-sky-950 duration-200 py-2"
            onClick={show}
          >
            Upload
          </button>
        </div>
      </div>
      <ImagePreview preview={preview} setPreview={setPreview} img={expand} />
    </div>
  );
};
